// API configuration and helper functions
export const getApiBaseUrl = () => {
  if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1') {
    return 'http://localhost:5000/api';
  } else {
    return 'https://tasselgroup-back.onrender.com/api';
  }
};

const API_BASE = getApiBaseUrl();

export const apiCall = async (endpoint, options = {}) => {
  const token = localStorage.getItem('token');
  const config = {
    headers: {
      'Content-Type': 'application/json',
      ...(token && { 'Authorization': `Bearer ${token}` }),
      ...options.headers
    },
    ...options
  };

  if (options.body && !['GET', 'HEAD'].includes(options.method?.toUpperCase() || 'GET')) {
    config.body = JSON.stringify(options.body);
  }

  try {
    console.log(`ðŸ“¡ Making API call to: ${API_BASE}${endpoint}`);
    const response = await fetch(`${API_BASE}${endpoint}`, config);

    const contentType = response.headers.get('content-type');
    let data;

    if (contentType && contentType.includes('application/json')) {
      data = await response.json();
    } else {
      const text = await response.text();
      throw new Error(`Server returned non-JSON response: ${text}`);
    }

    if (!response.ok) {
      throw new Error(data.message || `HTTP error! status: ${response.status}`);
    }

    return data;

  } catch (error) {
    console.error(`âŒ API call failed: ${error.message}`);
    if (error.message.includes('Failed to fetch')) {
      throw new Error('Cannot connect to server. Please check your internet connection and try again.');
    }
    throw error;
  }
};

// Specific API methods
export const apiService = {
  // Products
  getProducts: () => apiCall('/products'),
  
  // Services
  getServices: () => apiCall('/services'),
  createService: (serviceData) => apiCall('/services', { method: 'POST', body: serviceData }),
  
  // Bookings
  getBookings: (params = '') => apiCall(`/bookings${params}`),
  createBooking: (bookingData) => apiCall('/bookings', { method: 'POST', body: bookingData }),
  updateBookingStatus: (bookingId, status) => 
    apiCall(`/bookings/${bookingId}/status`, { method: 'PATCH', body: { status } }),
  assignStaffToBooking: (bookingId, staffId) =>
    apiCall(`/bookings/${bookingId}/assign-staff`, { method: 'PATCH', body: { staffId } }),
  
  // Gift Packages
  getGiftPackages: () => apiCall('/gift-packages'),
  createGiftOrder: (giftData) => apiCall('/gift-orders', { method: 'POST', body: giftData }),
  
  // Orders
  getOrders: (params = '') => apiCall(`/orders${params}`),
  createOrder: (orderData) => apiCall('/orders', { method: 'POST', body: orderData }),
  updateOrderStatus: (orderId, status) =>
    apiCall(`/orders/${orderId}/status`, { method: 'PATCH', body: { status } }),
  
  // Staff
  getStaffMembers: () => apiCall('/users/staff'),
  
  // Dashboard
  getAdminDashboard: () => apiCall('/dashboard/admin'),
  getStaffDashboard: () => apiCall('/dashboard/staff'),
  getCustomerOrders: () => apiCall('/dashboard/orders/my-orders'),
  getCustomerBookings: () => apiCall('/dashboard/bookings/my-bookings'),
  getCustomerGifts: () => apiCall('/dashboard/gift-orders/my-gifts'),
  
  // Payment
  initiatePayment: (orderData) => apiCall('/payment/initiate', { method: 'POST', body: orderData }),
  createManualOrder: (orderData) => apiCall('/payment/manual-order', { method: 'POST', body: orderData }),
  
  // Receipts
  generateReceipt: (type, id) => apiCall(`/dashboard/receipt/${type}/${id}`)
};
